client.on("messageCreate", (message) => {
  if (message.author.bot) return;
  const prefix = jsdb.get("prefix");
  if (message.content == prefix + "panel") {
    if (!message.member.permissions.has("Administrator"))
      return message.delete();
    const panelembed = new EmbedBuilder()
      .setDescription("قم باختيار المنتج الذي ستشتريه")
      .setColor("Random");
    const btn_role = new ButtonBuilder()
      .setCustomId("btn_role")
      .setLabel("رتبة بائع")
      .setStyle(ButtonStyle.Primary);
    const btn_room = new ButtonBuilder()
      .setCustomId("btn_proom")
      .setLabel("روم خاصة")
      .setStyle(ButtonStyle.Primary);
    const btn_manshor = new ButtonBuilder()
      .setCustomId("btn_manshor")
      .setLabel("منشور مميز")
      .setStyle(ButtonStyle.Primary);
    const btn_ad = new ButtonBuilder()
      .setCustomId("btn_ad")
      .setLabel("اعلان")
      .setStyle(ButtonStyle.Primary);
    const rowpanel = new ActionRowBuilder().addComponents(
      btn_role,
      btn_room,
      btn_manshor,
      btn_ad
    );
    message.delete();
    message.channel.send({
      content: `🌟 **${message.guild.name}**`,
      embeds: [panelembed],
      components: [rowpanel],
    });
  }
});

