/* ✅ open buy role ticket  */

client.on("interactionCreate", (interaction) => {
  if (interaction.customId == "btn_role") {
    const userroleid = interaction.user.id;

    const id = interaction.user.id;
    const ticketclient = userMention(id);

    const rolebget = db.get("roleticket");
    const rolest = db.get("rolebuy");
    if (rolest == "0")
      return interaction.reply({
        content: "عذرا بيع الرتب مقفل الان",
        ephemeral: true,
      });

    if (rolebget.includes(userroleid)) {
      interaction.reply({
        content: "عذرا لديك تكت مفتوحة بالفعل",
        ephemeral: true,
      });
    } else {
      db.push("roleticket", userroleid);
      const SupportRole = interaction.guild.roles.cache.get(
        config.buy_role.support_role_id
      );
      const categoryId = config.buy_role.category_id;
      var Id = Math.floor(Math.random() * 9999) + 1;
      interaction.guild.channels
        .create({
          name: `role-${Id}`,
          type: "0",
        })
        .then((channel) => {
          channel.setParent(categoryId);
          channel.permissionOverwrites.create(channel.guild.roles.everyone, {
            ViewChannel: false,
          });
          channel.permissionOverwrites.create(SupportRole, {
            ViewChannel: true,
            SendMessages: true,
          });
          channel.permissionOverwrites.create(interaction.user.id, {
            ViewChannel: true,
            SendMessages: true,
          });

          const id = interaction.user.id;
          const ticketclient = userMention(id);
          setTimeout(function () {
            channel.send({
              content: `
            ${ticketclient} مرحبا بك حبيبي 🌹
            لاكمال عملية الشراء اضغط على continue
            `,
              components: [
                {
                  type: 1,
                  components: [
                    {
                      type: 2,
                      style: 3,
                      label: "continue",
                      // Our button id, we can use that later to identify,
                      // that the user has clicked this specific button
                      custom_id: "rolecontinue",
                    },
                    {
                      type: 2,
                      style: 4,
                      label: "cancel",
                      // Our button id, we can use that later to identify,
                      // that the user has clicked this specific button
                      custom_id: "role_cancel",
                    },
                  ],
                },
              ],
            });
          }, 2500);
        });
      interaction.reply({
        content: `
        تم فتح تكت ✅
        اكمل بقية المراحل لشراء رتبة
        `,
        ephemeral: true,
      });
      const open_tick_embed = new EmbedBuilder()
        .setDescription(`${ticketclient} قام بفتح تكت شراء رتبة`)
        .setColor("#FFFF00")
        .setTimestamp();
      let logchdata = jsdb.get("buy_role");

    }
  }
});

