client.on("messageCreate", (message) => {
  const prefix = jsdb.get("prefix");
  if (message.content.startsWith(prefix + "set-prefix")) {
    const owners = config.owners;
    const userid = message.author.id;
    let args = message.content.toLowerCase().split(" ");

    if (message.author.bot) return;
    if (!owners.includes(userid))
      return message.reply("❌ لا تستطيع استخدام هذا الامر");
    if (!args[1]) return message.reply("❌ ضع البريفكس");
    if (args[1].length > 1) return message.reply("❌ البريفكس يكون رمز واحد");
    jsdb.set("prefix", args[1]);
    message.reply(`✅ تم تعديل البريفكس الى \`${args[1]}\``);
  }
});

client.on("messageCreate", (message) => {
  const prefix = jsdb.get("prefix");
  if (message.content.startsWith(prefix + "set-line")) {
    const owners = config.owners;
    const userid = message.author.id;
    let args = message.content.split(" ").slice(1).join(" ");

    if (message.author.bot) return;
    if (!owners.includes(userid))
      return message.reply("❌ لا تستطيع استخدام هذا الامر");
    if (!args) return message.reply("❌ ضع رابط الخط");

    jsdb.set("line", args);
    message.reply(`✅ تم تعديل الخط `);
  }
});

